DROP TABLE authorities;
CREATE TABLE authorities (id BIGINT NOT NULL PRIMARY KEY AUTO_INCREMENT, authority_name VARCHAR(50)  NOT NULL, authority_desc VARCHAR(200)  , authority_code VARCHAR(100) , authority_path VARCHAR(200) , authority_type INT, data_level INT, is_sys BIT, parent_id BIGINT, created_by VARCHAR(50) , created_date timestamp, last_modified_by VARCHAR(50) , last_modified_date timestamp);
