DROP TABLE users;
CREATE TABLE users (id BIGINT NOT NULL PRIMARY KEY AUTO_INCREMENT, username VARCHAR(50) NOT NULL, password VARCHAR(100) , nickname VARCHAR(50) , name VARCHAR(50) , email VARCHAR(255) , phone VARCHAR(255) , avatar VARCHAR(255) , activated BIT, activation_key VARCHAR(255) , reset_key VARCHAR(255) , reset_date TIMESTAMP, enabled BIT, created_by VARCHAR(50) , created_date TIMESTAMP, last_modified_by VARCHAR(50) , last_modified_date TIMESTAMP);
